SELECT a.user_id as id, a.username as username, a.nickname as nickname, c.role_name as roleName, d.dept_name as deptName, a.mobile as mobile, a.`status` as status, a.create_time as create_time FROM admin a JOIN admin_role b ON a.user_id = b.admin_id JOIN role c ON b.role_work_id = c.work_id JOIN dept d ON a.dept_id = d.dept_id WHERE a.isdel = 0
and a.username =#{userName}
and a.mobile = #{mobile}
and a.nickname = #{nickName}
and a.status = #{status}
and d.dept_name like concat('%', #{deptName}, '%')
and c.role_name like concat('%', #{roleName}, '%')
and a.create_time between #{createTimeStart} and #{createTimeEnd}
order by a.create_time desc
limit #{offsetIndex}, #{limit}

SELECT a.user_id as id, a.username as username, a.nickname as nickname, c.role_name as roleName, d.dept_name as deptName, a.mobile as mobile, a.`status` as status, a.create_time as create_time FROM admin a JOIN admin_role b ON a.user_id = b.admin_id JOIN role c ON b.role_work_id = c.work_id JOIN dept d ON a.dept_id = d.dept_id WHERE a.isdel = 0
and a.username =#{userName}
and a.mobile = #{mobile}
and a.nickname = #{nickName}
and a.status = #{status}
and d.dept_name like concat('%', #{deptName}, '%')
and c.role_name like concat('%', #{roleName}, '%')
and a.create_time between #{createTimeStart} and #{createTimeEnd}

SELECT
  count(1)
FROM
admin a
JOIN admin_role b ON a.user_id = b.admin_id
JOIN role c ON b.role_work_id = c.work_id
JOIN dept d ON a.dept_id = d.dept_id
WHERE a.isdel = 0

    and a.username =#{userName}


    and a.mobile = #{mobile}


    and a.nickname = #{nickName}


    and a.status = #{status}


    and d.dept_name like concat('%', #{deptName}, '%')


    and c.role_name like concat('%', #{roleName}, '%')


    and a.create_time between #{createTimeStart} and #{createTimeEnd}
